import drawTiles from './selectTileSource.js'

const reducer = (state, action) => {
	switch (action.type) {
		case 'SELECT_TILE_SOURCE':
			return selectTileSource(state, action.payload);
		case 'PUT_TILES_ON_WALL':
			return {
				...state,
				posts: state.posts.concat(action.payload)
			};
		default:
			return state;
	}
};

export default reducer;
