import getRandomInt from '../js_utils/getRandomInt.js'
import tileColSplitter from './tileColSplitter.js'

export default function fillRepos(state) {

	const newState = { ...state };

	for (let i in newState.repos) {
		let repoFilled = (
			newState.repos[i].filter(el => el !== newState.emptyTileField).length
			===
			newState.tilesPerRepo
			);
		while (!repoFilled) {
			const randId = getRandomInt(
				0,
				newState.bag.length,
				newState.tilesPerRepo - missingTiles(newState,i)
			);
			const splittedBag = tileColSplitter(newState.bag, randId);
			newState.repos[i] = splittedBag.drawn;
			newState.bag = splittedBag.rest;
			const bagHasEnoughTiles = (newState.bag.length >= newState.tilesPerRepo);
			if (bagHasEnoughTiles) {
				const mutatedProps = refillBag(newState);
				newState.bag = mutatedProps.bag;
				newState.usedTiles = mutatedProps.usedTiles;
			} else {
				repoFilled = true;
			}
		}
	}

	return newState
}

function refillBag(st) {
	return (
		{
			bag: st.usedTiles,
			usedTiles: [],
		}
	)
}

function missingTiles(st,i) {
	return st.repos[i].filter(el => el !== st.emptyTileField).length
}
