import React, { useContext } from 'react';
import './App.css';
import Store, {Context} from './Store.js'
import Player from './Player.js'
//import Center from './Center.js'

function App() {

  const state = useContext(Context);

  return (
    <Store >
      <div id='app-container'>
        {state.playerList.map((el, i) => {
          <Player playerId={i} />
        })}
        {/*<Center />*/}
      </div>
    </Store>
  );
}

export default App;
