import React, { useContext } from 'react';
import {Context} from './Store.js'
import './TileField.css';


function TileField(props) {
  const type = props.type ? props.type : 'tile-field';
  const row = props.row ? props.row : '';
  const column = props.column ? props.column : '';
  const repo = props.repo ? props.repo : '';
  //const parentRow = props.row ? props.row : '';

  const [state, dispatch] = useContext(Context);

  const occupancy = props.tileColor===state.emptyTileField
    ? 'empty'
    : 'tile-color-' + props.tileColor;

  return (
    <button 
    className={[type, row, column, occupancy].join(' ')}
    onClick={() => dispatch({
      type: 'SELECT_TILES',
      payload: {
        color: props.tileColor,
        repo: repo,
      },
    })}
    ></button>
  )
}

export default TileField
