  const nPlayer = props.playerList.length
  const nRepos = 2 * nPlayer + 1;
  const wallPattern = (props.playMode==='predefined') 
  ? Array.from(
    Array(props.nColors), 
    (y, j) => Array.from(
      Array(props.nColors),
      (x, i) => (props.Colors - i + j) % props.nColors
      )
    )
  : null;

  const [bag, setBag] = useState(
    Array(props.nColors).fill(props.tilesPerColor)
