  }
  for (let i = 0; i < model.nRepos; i++) {
    model = addRepoToModel(model, i);
  }*/

  return (
    <div id='app-container'>
      {props.playerList.map((el,i) => {
        <Player 
        id={i} 
        name={el} 
        at-turn={i===playerAtTurn ? true : false} />
      })}
      <Middle />
    </div>
  );
}

export default App;
