import React, { useContext } from 'react'
import {Context} from './Store.js'
import TileField from './TileField.js'
//import {} from './utils'
import './Rows.css'

function Rows(props) {

	const state = useContext(Context);

	return (
		<div className='rows-container'>
			{state[0].rows[props.playerId].map(
				(tileRow,rowId) => tileRow.map(
					(tileColor,columnId) => 
					<TileField 
					row={'row' + rowId} 
					column={'column' + ((-2) - columnId)} 
					color={tileColor} 
					/>
				)
			)}
		</div>
	)
}

export default Rows
