import React, { useContext } from 'react';
import { Context } from './Store.js'
import TileField from './TileField.js'
import './Wall.css'

function Wall(props) {

	const [state, disaptch] = useContext(Context);

	return (
		<div className='wall-container'>
			{state.wall[props.player].map(
				(tileRow,rowId) => tileRow.map(
					(color,columnId) => 
					<TileField 
					row={'row' + rowId} 
					column={'column' + columnId} 
					tileColor={color} 
					/>
				)
			)}
		</div>
	)
}

export default Wall
