import selectTileSource from './selectTiles.js'

const reducer = (state, action) => {
	switch (action.type) {
		case 'SELECT_TILES':
			return selectTiles(state, action.payload);
		case 'SELECT_TILE_TARGET':
			return {
				...state,
				posts: state.posts.concat(action.payload)
			};
		default:
			return state;
	}
};

export default reducer;
