import React, { useContext } from 'react'
import { Context } from './Store.js'
import TileField from './TileField.js'
import './Rows.css'

function Rows(props) {

	const state = useContext(Context);

	return (
		<div className='rows-container'>
			{state[0].rows[props.player].map(
				(tileRow, rowId) => tileRow.map(
					(color, columnId) =>
						<TileField
							row={'row' + rowId}
							column={'column' + ((state[0].nColor - 1) - columnId)}
							tileColor={color}
						/>
				)
			)}
		</div>
	)
}

export default Rows
