import React, {useContext} from 'react';
import {Context} from './Store.js'
import Repo from './Repo.js'
import DistViz from './DistViz.js';
import './Center.css'
import tileColToDist from '../azul_lib/tileColToDist.js';

function Center() {

	const state = useContext(Context);

	const middleDist = tileColToDist(state[0].repos.at(-1), state[0].nColor);

	return (
		<div id='center-container'>
			<div id='all-repos-container'>
				{state[0].repos.slice(0,-1).map((el, i) => <Repo key={i} repo={i} />)}
			</div>
			<div id='middle-tiles-container'>
				<DistViz place='middle' repo={state[0].nRepos-1} dist={middleDist}/>
			</div>
		</div>
	)
}

export default Center
