import {drawTiles} from './reducerFunctions.js'

//const actionTypes = {}//Why should one define this actionTypes object?

const Reducer = (state, action) => {
    switch (action.type) {
        case 'DRAW_TILES':
            return drawTiles(state,action.payload);
        case 'PUT_TILES_ON_WALL':
            return {
                ...state,
                posts: state.posts.concat(action.payload)
            };
        default:
            return state;
    }
};

export default Reducer;
