export function getRandomInt(min, max) {
	min = Math.ceil(min);
	max = Math.floor(max);
	return Math.floor(Math.random() * (max - min)) + min;
}

export function sumArray(numArray) {
	return numArray.reduce((acc, x) => acc + x, 0);
}

export function addArrays(numArray, addedArray) {
	return numArray.map((x, i) => x + addedArray[i]);
}

export function explicitTiles(dist) {
	return dist.map((x, i) => Array(x).fill(i)).flat();
}

export function tileColl(tileDist) {
	return dist.map((x, i) => Array(x).fill(i)).flat();
}

export function tileDist(tileColl, n) {
	return [...Array(n).keys()].map(i => tileColl.filter(el => el === i).length)
}
