export default function getRandomInt(min, max, n) {
	const nn = n===undefined ? 1 : n ;
	const minInt = Math.ceil(min);
	const maxInt = Math.floor(max);
	const sample = Array(nn).fill(0).map(() => Math.floor(Math.random() * (maxInt - minInt)) + minInt);
	return (
		nn === 1 ? sample[0] : sample
	);
}
