import { type } from '@testing-library/user-event/dist/type/index.js';
import gameState from '../azul_lib/gameState.js'

export default function startNewGame(state, payload) {

    const isInitialCall = (typeof(state)==='undefined');
    if (isInitialCall) {
        playerList = ['Player 1', 'Player 2'];
    } else {
        const playerList = state.playerNameInput;
    }
    const initialState = new gameState(playerList);

	return initialState
}
