import React, { useContext } from 'react'
import { Context } from './Store.js'
import './Info.css'


function Info(props) {

	const [state] = useContext(Context);

	const whoseTurn = 'It is ' + state.playerList[state.playerAtTurn].toString() + "'s turn:";
	let stateInfo = ''
	let whatToDo = '';
	if (state.hasOwnProperty('selectedTiles')) {
		let auxRepo = state.selectedTiles.repo==='middle'
		? 'the leftovers'
		: 'repository ' + state.selectedTiles.repo;
		stateInfo = 'Color' +
		state.selectedTiles.color + 
		'-tiles selected from ' +
		auxRepo + '.';
		whatToDo = 'Please select a target row!';
	} else if (!state.isGameOver) {
		stateInfo = 'No tiles selected.';
		whatToDo = 'Please select tiles from repository or leftovers!';
	} else {
		const maxScore = Math.max(...state.scores);
	    let winner = state.playerList.filter(
    	    (_,i) => state.scores[i] === maxScore
    	);
		stateInfo = 'The game is over. ' + winner + ' wins with ' + maxScore + 'points.';
		whatToDo = 'Please click below button to start a new game!';
	}

	return (
		<div id='info-container'>
			<output className='info-output'>{whoseTurn}</output>
			{stateInfo===''
			? null
			:<output className='info-output state-info'>{stateInfo}</output>}
			<output className='info-output what-to-do'>{whatToDo}</output>
			<button> Start new game </button>
		</div>
	)
}

export default Info
