import React, { useContext } from 'react'
import { Context } from './Store.js'
import NameInput from './NameInput.js'
import './NumPlayerSelection.css'

export default function NumPlayerSelection(props) {

	const [state, dispatch] = useContext(Context);

    return (
        <form className='playerForm'>
            <label htmlFor='nPlayer'>Select number of players:</label>
            <select 
                name='nPlayer' 
                id='nPlayer' 
                defaultValue={state.numPlayerDefault} 
                onChange={e => {

                }}
            >
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
            </select>
            <NameInput numInputs={state.numPlayerSelected}/>
        </form>
    )
}

callBack = event => {
    dispatch({
        type: 'SELECT_NUM_PLAYER',
        payload: {
            numPlayerSelected: event.target.value,
        }
    });
    [...Array(event.target.value).keys()].forEach(
        i => dispatch({
            type: 'SET_NAME',
            payload: {
                player: i,
                playerName: document.getElementById("inputName" + i).value,
            }
        })
    )
}
