import React, { useContext, useState } from 'react'
import { Context } from './Store.js'
import './NameInput.css'

export default function NameInput(props) {

	const [state, dispatch] = useContext(Context);
    const numPlayerdefault = 2;
    const [numPlayer, setNumPlayer] = useState(numPlayerdefault);
    console.log(numPlayer);

    if (state.isGameOver) {
        return
    }

    return (
        <form>
            <label htmlFor='nPlayer'>Select number of players:</label><br/>
            <select 
                name='nPlayer' 
                id='nPlayer' 
                defaultValue={numPlayerdefault} 
                onChange={
                    e => setNumPlayer(e.target.value)
                }
            >
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
            </select><br/>
            <label htmlFor='name1'>Name of Player 1:</label><br/>
            <input type='text' id='name1' name='name1'/><br/>
            <label htmlFor='name2'>Name of Player 2:</label><br/>
            <input type='text' id='name2' name='name2'/><br/>
            <label htmlFor='name3' style={maybeHide(numPlayer,[2])}>Name of Player 3:</label><br/>
            <input type='text' id='name3' name='name3' style={maybeHide(numPlayer,[2])}/><br/>
            <label htmlFor='name4' style={maybeHide(numPlayer,[2,3])}>Name of Player 4:</label><br/>
            <input type='text' id='name4' name='name4' style={maybeHide(numPlayer,[2,3])}/><br/>
        </form>
    )
}

function maybeHide(numPlayer, toBeHiddenFor) {
    const hide = toBeHiddenFor.includes(numPlayer);
    if (hide) {
        return (
            {display: 'none'}
        )
    } else {
        return (
            {display: 'block'}
        )
    }
}
