import React, { useContext } from 'react'
import { Context } from './Store.js'
import './DistViz.css'

function DistViz(props) {

	const [state, dispatch] = useContext(Context);

	const size = props.place==='middle' ? 'larger-size' : 'normal-size';

	return (
		<div className='dist-viz-container'>
			{props.dist.map(
				(el,i) => <button className={
					['dist-viz-tile', 'tile-color-' + i, size].join(' ')
				} >{el}</button>
			)}
			{state.startingTile===props.place
				? <button 
				id='starting-tile' 
				className={['dist-viz-tile', size].join(' ')} 
				>{1}</button> 
				: null}
		</div>
	)
}

export default DistViz
