import {tileDist, addArrays} from './utils.js'

export function drawTiles(state, payload) {

	const player = payload.player;
	const color = payload.color;
	const tileDist = payload.repo==='middle'
	? state.middle[player]
	: tileDist(state[payload.repo]);

	if (tileDist[color] > 0) {
		state[player].drawn = Array(NUM_COLORS).fill(0);
		state[player].drawn[color] = tileDist[color];
		tileDist[color] = 0;
		if (repo === "middle") {
			if (state.startingTile === "middle") {
				state.startingTile = player;
			}
		} else {
			state.middle = addArrays(state.middle, tileDist);
			tileDist = Array(NUM_COLORS).fill(0);
		}
		let drawnColor = allOfOneColor(state[player].drawn);
		if (!drawnColor.allOfOne) {
			console.log("Detected more than one color.")
		}
		if (drawnColor.color === -1) {
			console.log("No color was drawn.")
		}
		return state;
	} else {
		//TODO: Think about what should happen here
		throw new Error(repo + " contains no tile of color " + color.toString() + "!");
	}
}
