export default function getRandomInt(min, max, n) {
	const nn = (n === undefined ? 1 : n);
	const minInt = Math.ceil(min);
	const maxInt = Math.floor(max);
	if (maxInt-minInt > nn) {
		throw new error('')
	}
	//const sample = Array(nn).fill(0).map(() => Math.floor(Math.random() * (maxInt - minInt)) + minInt);
	const sample = Array(nn);
	for (let i = 0; i < nn; i++) {
		let filled = false;
		while (!filled) {
			const newRand = Math.floor(Math.random() * (maxInt - minInt)) + minInt;
			if (sample.findIndex(el => el === newRand) === -1) {
				sample[i] = newRand;
				filled = true;
			}
		}
	}
	return (
		nn === 1 ? sample[0] : sample
	);
}
