import addArrays from '../js_utils/addArrays.js'
import tileColToDist from '../azul_lib/tileColToDist.js'

export default function putTilesIntoRow(state, payload) {

	const newState = { ...state };

	const drawn = newState.repos[newState.selectedTiles.repo].filter(el => el===newState.selectedTiles.color);
	const rest = tileColToDist(newState.repos[newState.selectedTiles.repo].filter(el => el!==newState.selectedTiles.color), newState.nColor);
	const targetRow = newState.rows[newState.playerAtTurn][payload.row];
	const newTargetRow = addDrawnToTarget(drawn, targetRow, newState.emptyTileField);

	newState.rows[newState.playerAtTurn][payload.row] = newTargetRow.newRow;
	newState.penalties[newState.playerAtTurn] = addArrays(newState.penalties[newState.playerAtTurn],newTargetRow.penalties);
	newState.middle = addArrays(newState.middle, rest)
	newState.repos[newState.selectedTiles.repo] = Array(newState.tilesPerRepo).fill(newState.emptyTileField);

	return newState
}

function addDrawnToTarget(drawn, targetRow, st) {
	const nTileInRow = targetRow.filter(el => el===drawn[0]).length;
	const nDrawn = drawn.length;
	const auxFilled = Array(Math.min(nTileInRow+nDrawn,targetRow.length)).fill(drawn[0]);
	const auxEmpty = Array(targetRow.length-auxFilled.length).fill(emptyTileField);
	return {
		newRow: auxFilled.concat(auxEmpty),
		penalties: tileColToDist(Array(Math.max(0,nDrawn-(targetRow.length-nTileInRow))).fill(drawn[0]), newState.nColor),
	}
}

