import React, { useGlobal } from 'reactn'


function TileField() {
  const [tileColor, setTileColor] = useGlobal('tileColor');

  return (
    <button
    id="bbb"
    className="blue"
    onClick={
    (e) => e.target.style.backgroundColor===tileColor ? setTileColor("#DC143C") : setTileColor(tileColor)
    }>
      Hello world!
    </button>
  )
}

export default TileField
