import React, {useContext} from 'react';
import { Context } from './Store.js'
import PlayerHeader from './PlayerHeader.js'
import Rows from './Rows.js'
import Wall from './Wall.js'
import Penalties from './Penalties.js'
import './Player.css'

function Player(props) {

	const [state, dispatch] = useContext(Context);
	const atTurn = state.playerAtTurn=== props.playerId ? 'at-turn' : '';

	return (
		<div
			id={'player' + props.playerId}//needed?
			className={'player-container ' + atTurn}
		>
			<PlayerHeader playerId={props.playerId} />
			<Rows playerId={props.playerId}/>
			<Wall playerId={props.playerId}/>
			<Penalties playerId={props.playerId}/>
		</div>
	)
}

export default Player
