import React, { useContext, useState } from 'react'
import { Context } from './Store.js'
import './NameInput.css'

export default function NameInput(props) {

	const [state, dispatch] = useContext(Context);
    //const numPlayerdefault = 2;
    //const [numPlayer, setNumPlayer] = useState(numPlayerdefault);

    if (state.isGameOver) {
        return
    }

    return (
        <form>
            <label htmlFor='nPlayer'>Select number of players:</label><br/>
            <select 
                name='nPlayer' 
                id='nPlayer' 
                value={2} 
                onChange={
                    e => dispatch({
                        type: 'SELECT_NUM_PLAYER',
                        payload: {
                            numPlayerSelected: e.target.value,
                        }
                    })
                }
            >
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
            </select><br/>
            {[...Array(state.numPlayerSelected).keys()].map( 
                el => (
                    <React.Fragment>
                        <label 
                            key={'label' + el} 
                            htmlFor={'name' + el}
                        >Name of Player {el}:</label>
                        <input 
                            type='text' 
                            key={'input' + el} 
                            id={'name' + el} 
                            name={'name' + el}
                            onChange={
                                e => dispatch({
                                    type: 'SET_NAMES',
                                    payload: {
                                        numPlayerSelected: e.target.value,
                                    }
                                })
                            }
                        />
                    </React.Fragment>
                )
            )}
        </form>
    )
}
