import React, { createContext, useReducer } from "react";
import Reducer from './Reducer'
import { getRandomInt } from './utils.js'

const TILES_PER_REPO = 4;
const N_COLORS = 5;
const TILES_PER_COLOR = 20;
const N_SHOWN_PENALTIES = 7;
const EMPTY_TILE_FIELD = -1;

const playerList = ['player1', 'player2', 'player3', 'player4'];
const nPlayer = playerList.length;
const nRepos = 2 * nPlayer + 1;

const initialState = {
	playerList: playerList,
	nPlayer: nPlayer,
    nRepos: nRepos,
	scores: Array(nPlayer).fill(0),
	rows: Array(nPlayer).fill(Array.from(Array(N_COLORS), (x,i) => Array(i+1).fill(EMPTY_TILE_FIELD))),
	tilesOnWall: Array(nPlayer).fill(Array.from(Array(N_COLORS), x => Array(N_COLORS).fill(EMPTY_TILE_FIELD))),
	penalties: Array(nPlayer).fill(Array(N_COLORS).fill(0)),
	bag: Array(N_COLORS).fill(TILES_PER_COLOR),
	usedTiles: Array(N_COLORS).fill(0),
	middle: Array(N_COLORS).fill(0),
	repos: Array(nRepos).fill(Array(TILES_PER_REPO).fill(EMPTY_TILE_FIELD)),
	startingTile: 'middle',
	playerAtTurn: getRandomInt(0, nPlayer),
	playMode: "predefined",
	wallPattern: Array.from(
		Array(N_COLORS),
		(y, j) => Array.from(
			Array(N_COLORS),
			(x, i) => (N_COLORS - i + j) % N_COLORS
		)
	),
	nColor: N_COLORS,
};

const Store = ({children}) => {

	const [state, dispatch] = useReducer(Reducer, initialState);
	return (
		<Context.Provider value={[state, dispatch]}>
			{children}
		</Context.Provider>
	)
};

export const Context = createContext(initialState);
export default Store;
