export default function tileColToDist(tileCol, nColor) {
	if (typeof(nColor)==='undefined') {
		throw new Error('tileColToDist: Number of colors has to be given')
	}
	return (
		[...Array(nColor).keys()].map(i => tileCol.filter(el => (el === i)).length)
	);
}
