import getRandomInt from '../js_utils/getRandomInt.js';
//import fillRepos from './fillRepos.js'

function gameState(
	N_COLORS,
	TILES_PER_REPO,
	TILES_PER_COLOR,
	EMPTY_TILE_FIELD,
	PLAYER_LIST
) {
	//game hyperparameters
	const nPlayer = PLAYER_LIST.length;
	const nRepos = 2 * nPlayer + 1;
	this.nColor = N_COLORS;
	this.tilesPerRepo = TILES_PER_REPO;
	this.tilesPerColor = TILES_PER_COLOR;
	this.emptyTileField = EMPTY_TILE_FIELD;
	this.playerList = PLAYER_LIST;

	//player specific state
	this.nPlayer = nPlayer;
	this.scores = Array(nPlayer).fill(0);
	this.rows = Array(nPlayer).fill(
		Array.from(Array(N_COLORS), (_, i) => Array(i + 1).fill(EMPTY_TILE_FIELD))
	);
	this.wall = Array(nPlayer).fill(
		Array.from(
			Array(N_COLORS), _ => Array(N_COLORS).fill(EMPTY_TILE_FIELD)
		)
	);
	this.penalties = Array(nPlayer).fill(Array(N_COLORS).fill(0));

	//state shared between players
	this.nRepos = nRepos;
	this.bag = Array.from(
		Array(N_COLORS), (_, i) => Array(TILES_PER_COLOR).fill(i)
	).flat();
	this.usedTiles = [];
	this.middle = Array(N_COLORS).fill(0);
	this.repos = Array(nRepos).fill().map(_ => Array(TILES_PER_REPO).fill(EMPTY_TILE_FIELD));
	this.startingTile = 'middle';
	this.playerAtTurn = getRandomInt(0, nPlayer);
	this.playMode = "predefined";
	this.wallPattern = Array.from(
		Array(N_COLORS),
		(_, j) => Array.from(
			Array(N_COLORS),
			(_, i) => (N_COLORS - i + j) % N_COLORS
		)
	);
};


export default gameState
