import React, { useContext } from 'react';
import {Context} from './Store.js'
import './TileField.css';


function TileField(props) {
  const type = props.type ? props.type : 'tile-field';
  const row = props.row ? props.row : '';
  const column = props.column ? props.column : '';
  const parentRepo = props.repo ? props.repo : null;

  const [state, dispatch] = useContext(Context);

  const occcupState = props.tileColor>state.emptyTileField
    ? 'occupied tile-color-' + props.tileColor
    : 'empty';

  return (
    <button 
    className={[type, row, column, occcupState].join(' ')}
    onClick={() => dispatch({
      type: 'DRAW_TILES',
      payload: {
        color: props.tileColor,
        repo: parentRepo,
      },
    })}
    ></button>
  )
}

export default TileField
