import { tileColl, addArrays } from './utils.js'

export function drawTiles(state, payload) {

	const player = payload.player;
	const color = payload.color;
	const tiles = payload.repo === 'middle'
		? tileColl(state.middle[player])
		: state[payload.repo];

	const newState = { ...state };

	if (tiles[color] > 0) {
		newState.drawn = tiles.filter(c => c === color);
		newState[payload.repo] = [];
		if (payload.repo === 'middle' && state.startingTile === 'middle') {
				state.startingTile = player;
		} else {
			state.middle = addArrays(state.middle, tiles);
			//tiles = Array(newState.nColors).fill(0);
		}
		/*let drawnColor = allOfOneColor(state[player].drawn);
		if (!drawnColor.allOfOne) {
			console.log("Detected more than one color.")
		}
		if (drawnColor.color === -1) {
			console.log("No color was drawn.")
		}*/
		return newState;
	} else {
		//TODO: Think about what should happen here
		throw new Error(payload.repo + " contains no tile of color " + color.toString() + "!");
	}
}
