import addArrays from '../js_utils/addArrays.js'
import tileColToDist from '../azul_lib/tileColToDist.js'
import tileDistToCol from '../azul_lib/tileDistToCol.js'

export default function drawTiles(state, payload) {

	const color = payload.color;
	const tiles = (payload.repo === 'middle')
		? tileDistToCol(state.middle)
		: state.repos[payload.repo];

	const newState = { ...state };

	newState.drawn = tiles.filter(c => c === color);

	if (newState.drawn.length > 0) {
		if (payload.repo === 'middle') {
			newState[payload.repo] = Array(newState.nColors).fill(0);
			if (newState.startingTile === 'middle') {
				newState.startingTile = newState.playerAtTurn;
			}
		} else {
			newState.middle = addArrays(newState.middle, tileColToDist(tiles.filter(c => c !== color)));
			newState[payload.repo] = [];
		}
		return newState;
	} else {
		alert(payload.repo + " contains no tile of color " + color.toString() + "!");
	}
}
