import React, { useContext } from 'react';
import {Context} from './Store.js'
import './TileField.css';


function TileField(props) {
  let type = props.type ? props.type : 'tile-field';
  let row = props.row ? props.row : '';
  let column = props.column ? props.column : '';

  const [state,dispatch] = useContext(Context);

  let occcupState = props.tileColor>state.emptyTileField
    ? 'occupied tile-color-' + props.tileColor
    : 'empty';

  return (
    <button 
    className={[type, row, column, occcupState].join(' ')}
    onClick={dispatch({
      type: 'DRAW_TILES',
      payload: {
        color: props.tileColor,
        repo: props.repo,
      },
    })}
    ></button>
  )
}

export default TileField
