import React, { useContext } from 'react'
import { Context } from './Store.js'
import './Info.css'


function Info(props) {

	const [state] = useContext(Context);
	let message = ''
	if (state.hasOwnProperty('selectedTiles')) {
		message = 
		state.playerList[state.playerAtTurn] + 
		' selected color ' +
		state.selectedTiles.color + 
		' tiles from ' +
		state.selectedTiles.repo;
	} else {
		message = 'It is ';
	}
	console.log(message)

	return (
		<div id='info-container'>
		<output className='output'>{message}</output>
		</div>
	)
}

export default Info
