import React, { useContext } from 'react';
import './App.css';
import Store, {Context} from './Store.js'
import Player from './Player.js'
//import Middle from './Middle.js'

function App() {

  const state = useContext(Context);

  console.log(state.playerList.map((el, i) => <Player ithPlayer={'player' + i} name={el}/>))

  return (
    <Store >
      <div id='app-container'>
        {/*<Player ithPlayer='player1' name='Hans'/>*/}
        {state.playerList.map((el, i) => <Player ithPlayer={'player' + i} name={el}/>)}
        {/*<Middle />*/}
      </div>
    </Store>
  );
}

export default App;
