import sumArray from '../js_utils/sumArray.js'

export default function calcPenalty(nTiles) {
	return (
		sumArray(Array(nTiles).fill(0).map(
			(x, i) => Math.min(3, Math.ceil((i + 2) / 3))
		))
	)
}
