import React, { useContext } from 'react'
import { Context } from './Store.js'
import './Info.css'


function Info(props) {

	const [state] = useContext(Context);
	let messages = '';
	if (state.hasOwnProperty('selectedTiles')) {
		message = 
		state.playerList[state.playerAtTurn] + 
		' selected color ' +
		state.selectedTiles.color + 
		' tile(s) from ' +
		state.selectedTiles.repo;
	} else {
		message = 
		state.playerList[state.playerAtTurn] +
		' should select tiles';
	} 	
	console.log(message)

	return (
		<div id='info-container'>
		{messages.map(el => <output className='output'>el</output>)}
		</div>
	)
}

export default Info
