import React, { useContext } from 'react';
import {Context} from './Store.js'
import './TileField.css';


function TileField(props) {
  let row = props.row ? props.row : '';
  let column = props.column ? props.column : '';
  let size = props.size ? 'size-large' : '';

  const state = useContext(Context);

  let occcupState = props.tileColor>state[0].emptyTileField
    ? 'occupied tile-color-' + props.tileColor
    : 'empty';

  return (
    <button className={[size, occcupState, row, column].join(' ')}></button>
  )
}

export default TileField
