import React, { createContext, useReducer } from 'react';
import reducer from '../reducer/reducer.js'
import matchState from '../azul_utils/matchState.js'
import fillRepos from '../azul_utils/fillRepos.js';

//TODO: fetch these from landing page
const TILES_PER_REPO = 4;
const N_COLORS = 5;
const TILES_PER_COLOR = 20;
const EMPTY_TILE_FIELD = -1;
const PLAYER_LIST = ['player1', 'player2', 'player3', 'player4'];

const initialState = fillRepos(
	new matchState(
		N_COLORS,
		TILES_PER_REPO,
		TILES_PER_COLOR,
		EMPTY_TILE_FIELD,
		PLAYER_LIST
	)
);


const Store = ({ children }) => {

	const [state, dispatch] = useReducer(reducer, initialState);
	return (
		<Context.Provider value={[state, dispatch]}>
			{children}
		</Context.Provider>
	)
};

export const Context = createContext(initialState);
export default Store;
