import selectTiles from './selectTiles.js'
import selectTileTarget from './selectTileTarget.js'


const reducer = (state, action) => {
	switch (action.type) {
		case 'SELECT_TILES':
			return selectTiles(state, action.payload);
		case 'SELECT_TILE_TARGET':
			return selectTileTarget(state, action.payload)
		default:
			return state;
	}
};

export default reducer;
