import getRandomInt from '../js_utils/getRandomInt.js'
import tileColSplitter from './tileColSplitter.js'

export default function fillRepos(state) {

	const newState = { ...state };

	for (let i in newState.repos) {
		let repoFilled = (nMissingInRepo(newState,i)===0);
		while (!repoFilled) {
			const randId = getRandomInt(
				0,
				newState.bag.length,
				nMissingInRepo(newState,i)
			);
			newState.repos[i] = newState.bag.filter((_,i) => randId.findIndex(el => el===i)>-1);
			newState.bag = newState.bag.filter((_,i) => randId.findIndex(el => el===i)==-1);
			repoFilled = true;
			const bagHasEnoughTiles = (newState.bag.length >= newState.tilesPerRepo);
			if (!bagHasEnoughTiles) {
				const mutatedProps = refillBag(newState);
				newState.bag = mutatedProps.bag;
				newState.usedTiles = mutatedProps.usedTiles;
			}
		}
	}

	return newState
}

function refillBag(st) {
	return (
		{
			bag: st.usedTiles,
			usedTiles: [],
		}
	)
}

function nMissingInRepo(st,i) {
	const nActual = st.repos[i].filter(el => el !== st.emptyTileField).length;
	const nTarget = st.tilesPerRepo;
	return nTarget-nActual
}
