import React, {useContext} from 'react';
import {Context} from './Store.js'
import Repo from './Repo.js'
import DistViz from './DistViz.js';
import './Center.css'
import tileColToDist from '../azul_lib/tileColToDist.js';

function Center() {

  const [state, dispatch] = useContext(Context);

	//let middleDist = state.middle;
	const idMiddle = state.repo.length;
	const middleDist = tileColToDist(state.repo[idMiddle]);

	return (
		<div id='center-container'>
			<div id='all-repos-container'>
				{state[0].repos.map((el, i) => <Repo key={i} repo={i} />)}
			</div>
			<div id='middle-tiles-container'>
				<DistViz place='middle' dist={middleDist}/>
			</div>
		</div>
	)
}

export default Center
