export function getRandomInt(min, max, n) {
	const minInt = Math.ceil(min);
	const maxInt = Math.floor(max);
	const sample = Array(n).fill(0).map(() => Math.floor(Math.random() * (maxInt - minInt)) + minInt);
	return (
		n === 1 ? sample[0] : sample
	);
}

export function sumArray(numArray) {
	return (
		numArray.reduce((acc, x) => acc + x, 0)
	);
}

export function addArrays(numArray, addedArray) {
	return (
		numArray.map((x, i) => x + addedArray[i])
	);
}

export function tileColl(tileDist) {
	return (
		tileDist.map((x, i) => Array(x).fill(i)).flat()
	);
}

export function tileDist(tileColl, n) {
	return (
		[...Array(n).keys()].map(i => tileColl.filter(el => (el === i)).length)
	);
}

export function getRandomTilesFromBag(bag, n) {
	const randInts = getRandomInt(0, bag.length - 1, n);
	return (
		randInts.map((el) => bag[el])
	)
}
