import React, { useContext } from 'react'
import { Context } from './Store.js'
import './Info.css'


function Info(props) {

	const [state] = useContext(Context);
	const whoseTurn = 'It is ' + state.playerList[state.playerAtTurn] + "'s turn:";
	let stateInfo = ''
	let whatToDo = '';
	if (state.hasOwnProperty('selectedTiles')) {
		stateInfo = state.selectedTiles.color + 
		'-tiles selected from ' +
		state.selectedTiles.repo;
		whatToDo = 'Please select a target row!';
	} else {
		stateInfo = '';
		whatToDo = 'Please select tiles!';
	}

	return (
		<div id='info-container'>
			<h4 className='info-output'>{whoseTurn}</h4>
			<output className='info-output what-to-do'>{whatToDo}</output>
			{messages.map(el => <output className='info-output'>{el}</output>)}
		</div>
	)
}

export default Info
