import React, { createContext, useReducer } from "react";
import Reducer from './Reducer'
import { getRandomInt } from './utils.js'

const TILES_PER_REPO = 4;
const N_COLORS = 5;
const TILES_PER_COLOR = 20;
const N_SHOWN_PENALTIES = 7;

const initialState = {
	playerList: ['player1', 'player2', 'player3', 'player4'],
	nPlayer: this.playerList.length,
    nRepos: 2 * this.nPlayer + 1,
	scores: Array(this.nPlayer).fill(0),
	waitingRows: Array(this.nPlayer).fill(Array.from(Array(N_COLORS), x => Array(N_COLORS).fill(0))),
	tilesOnWall: Array(this.nPlayer).fill(Array.from(Array(N_COLORS), x => Array(N_COLORS).fill(-1))),
	penalties: Array(this.nPlayer).fill(Array(N_COLORS).fill(0)),
	bag: Array(N_COLORS).fill(TILES_PER_COLOR),
	usedTiles: Array(N_COLORS).fill(0),
	middle: Array(N_COLORS).fill(0),
	repos: Array(this.nRepos).fill(Array(N_COLORS).fill(0)),
	startingTile: 'middle',
	playerAtTurn: getRandomInt(0, this.nPlayer),
	playMode: "predefined",
	wallPattern: Array.from(
		Array(N_COLORS),
		(y, j) => Array.from(
			Array(N_COLORS),
			(x, i) => (N_COLORS - i + j) % N_COLORS
		)
	),
};

const Store = ({children}) => {

	const [state, dispatch] = useReducer(Reducer, initialState);
	return (
		<Context.Provider value={[state, dispatch]}>
			{children}
		</Context.Provider>
	)
};

export const Context = createContext(initialState);
export default Store;
