import { tileColl, tileDist, addArrays } from './utils.js'

export function drawTiles(state, payload) {

	const color = payload.color;
	const tiles = payload.repo === 'middle' ? tileColl(state.middle) : state.repos[payload.repo];

	const newState = { ...state };

	newState.drawn = tiles.filter(c => c === color);
if(newState.drawn.length>0) {
	if (payload.repo === 'middle') {
		newState[payload.repo] = Array(newState.nColors).fill(0);
		if (newState.startingTile === 'middle') {
			newState.startingTile = newState.playerAtTurn;
		}
	} else {
		newState.middle = addArrays(newState.middle, tileDist(tiles.filter(c => c !== color)));
		newState[payload.repo] = [];
	}
	/*if (!drawnColor.allOfOne) {
		console.log("Detected more than one color.")
	}
	if (drawnColor.color === -1) {
		console.log("No color was drawn.")
	}*/
	return newState;
} else {
	//TODO: Think about what should happen here
	alert(payload.repo + " contains no tile of color " + color.toString() + "!");
}
}
