import getRandomInt from '../js_utils/getRandomInt.js';
import fillRepos from './fillRepos.js'

function matchState(
	N_COLORS,
	TILES_PER_REPO,
	TILES_PER_COLOR,
	EMPTY_TILE_FIELD,
	PLAYER_LIST
) {
	const nPlayer = PLAYER_LIST.length;
	const nRepos = 2 * nPlayer + 1;

	this.nColor = N_COLORS;
	this.nTilesPerRepo = TILES_PER_REPO;
	this.tilesPerColor = TILES_PER_COLOR;
	this.emptyTileField = EMPTY_TILE_FIELD;
	this.playerList = PLAYER_LIST;

	this.nPlayer = nPlayer;
	this.nRepos = nRepos;
	this.scores = Array(nPlayer).fill(0);
	this.rows = Array(nPlayer).fill(Array.from(Array(N_COLORS), (x, i) => Array(i + 1).fill(EMPTY_TILE_FIELD)));
	this.wall = Array(nPlayer).fill(Array.from(Array(N_COLORS), x => Array(N_COLORS).fill(EMPTY_TILE_FIELD)));
	this.penalties = Array(nPlayer).fill(Array(N_COLORS).fill(0));
	//this.penaltyValues = Array(nPlayer).fill(0);//

	this.bag = Array(N_COLORS).fill(TILES_PER_COLOR);
	this.usedTiles = Array(N_COLORS).fill(0);
	this.middle = Array(N_COLORS).fill(0);
	this.repos = Array(nRepos).fill(Array(TILES_PER_REPO).fill(EMPTY_TILE_FIELD));
	this.startingTile = 'middle';
	this.playerAtTurn = getRandomInt(0, nPlayer);
	this.playMode = "predefined";
	this.wallPattern = Array.from(
		Array(N_COLORS),
		(y, j) => Array.from(
			Array(N_COLORS),
			(x, i) => (N_COLORS - i + j) % N_COLORS
		)
	);
};


export default matchState
