import React, { useContext } from 'react'
import {Context} from './Store.js'
import TileField from './TileField.js'
import './Repo.css'

function Repo(props) {

	const [state, dispatch] = useContext(Context);

	return (
		<div id={'repo' + props.repoId} className='repo-container'>
			{state.repos[props.repoId].map((el) => <TileField color={'color' + el} />)}
		</div>
	)
}

export default Repo
