import React, { useContext } from 'react'
import { Context } from './Store.js'
import './DistViz.css'

function DistViz(props) {

	const [state, dispatch] = useContext(Context);

	const size = (props.place === 'middle') ? 'larger-size' : 'normal-size';

	let callBack = {type: ''};
	if (props.type==='repo' && occupancy!=='empty') {
		callBack = {
		  type: 'SELECT_TILES',
		  payload: {
			color: props.tileColor,
			repo: props.repo,
		  }
		};
	}
	return (
		<div className='dist-viz-container'>
			{props.dist.map(
				(el, i) => {
					return (
						<button
						    key={i} 
							className={['dist-viz-tile', 'tile-color-' + i, size].join(' ')} 
							onClick={() => dispatch({
								type: 'SELECT_TILES',
								payload: {
									color: i,
									repo: state.repos.length-1,
								}
							})}
						>{el}</button>
					)
				}
			)}
			{state.startingTile === props.place
				? <button
					id='starting-tile'
					className={['dist-viz-tile', size].join(' ')}
				>{1}</button>
				: null
			}
		</div >
	)
}

export default DistViz
