import React, { useContext } from 'react'
import { Context } from './Store.js'
import TileField from './TileField.js'
import './Penalties.css'

function DistViz(props) {

	const state = useContext(Context);

	return (
		<div className='dist-viz'>
			{state[0].startingTile===props.place
				? <button id='starting-tile' className={'dist-viz'} >{1}</button> 
				: null}
				{props.dist.map(
					(el,i) => <button className={'dist-viz tile-color-' + i} >{el}</button>
				)}
		</div>
	)
}

export default DistViz
