import React, { useContext } from 'react'
import Context from './Store.js'
import './Repo.css'

function Repo(props) {

	const state = useContext(Context);

	return (
		<div id={'repo' + props.repoId} className='repo-container'>
			{state[0].repos[props.repoId].map((el) => <button className={'tile-button color' + el}></button>)}
		</div>
	)
}

export default Repo
